/*! Lightweight Chart.js loader */
(function(){
  var CDN='https://cdn.jsdelivr.net/npm/chart.js@4.4.4/dist/chart.umd.min.js';
  if(window.Chart){return;}
  var script=document.createElement('script');
  script.src=CDN;
  script.async=true;
  script.crossOrigin='anonymous';
  script.onload=function(){console.info('Chart.js loaded from CDN');};
  script.onerror=function(){console.error('Unable to load Chart.js from '+CDN);};
  document.head.appendChild(script);
})();

